package chenjie.leetcode.doublepointer;

import org.junit.Assert;
import org.junit.Test;

import java.util.HashMap;
import java.util.Map;
//567. 字符串的排列
public class CheckInclusion {
    public boolean checkInclusion(String s1, String s2) {
        int windowsSize = s1.length();
        int left = 0;
        Map<Character, Integer> target = new HashMap<>();

        for (int i = 0; i < windowsSize; i++) {
            target.compute(s1.charAt(i), (key, value) -> value == null ? 1 : value + 1);
        }
        Map<Character, Integer> windows = new HashMap<>();

        for (int right = 0; right < s2.length(); right++) {
            char cur = s2.charAt(right);
            if (target.containsKey(cur)) {
                windows.compute(cur, (key, value) -> value == null ? 1 : value + 1);
            }

            while (right - left + 1 > windowsSize) {
                char leftChar = s2.charAt(left);
                if (target.containsKey(leftChar)) {
                    windows.compute(leftChar, (k, v) -> v == 1 ? null : v - 1);
                }

                left++;
            }
            if (check(windows, target)) {
                return true;
            }
        }
        return false;
    }

    private boolean check(Map<Character, Integer> window, Map<Character, Integer> target) {
        for (Map.Entry<Character, Integer> entry : target.entrySet()) {
            //error： 对象比较， 不能这样比
            //if (window.getOrDefault(entry.getKey(), 0) != entry.getValue())  {
            if (window.getOrDefault(entry.getKey(), 0).compareTo(entry.getValue()) != 0) {
                return false;
            }
        }
        return true;
    }

    @Test
    public void test001() {
        String s1 = "";
        String s2 = "";
        Assert.assertTrue(checkInclusion(s1, s2));
    }

    @Test
    public void test002() {
        String str1 = "aa";
        String str2 = "aaaaa";
        Assert.assertTrue(checkInclusion(str1, str2));

    }
}
